import "../assets/styles/login.scss";
import { Row, Col, Form, Button, Modal, Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import React, { useState } from "react";

const SignUpConst = () => {
    const [nome, setNome] = useState("");
    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");
    const [confirmarSenha, setConfirmarSenha] = useState("");
    const [emailError, setEmailError] = useState("");
    const [formModal, setFormModal] = useState(false);
    const [successModal, setSuccessModal] = useState(false);
    const [errorModal, setErrorModal] = useState(false);
    const [passcheckModal, setPasscheckModal] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (senha !== confirmarSenha) {
            setPasscheckModal(true);
            return;
        }
        if (senha === "" || email === "" || nome === "") {
            setErrorModal(true);
            return;
        }

        // Verifique se o email já foi inserido pelo usuário
        const isEmailUnique = await checkEmailUniqueness(email);

        if (!isEmailUnique) {
            setEmailError("Este email já está cadastrado.");
            setErrorModal(true);
            return;
        }

        try {
            const response = await fetch("http://localhost:9000/signup", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ nome, email, senha }),
            });

            if (response.ok) {
                setSuccessModal(true); // Exibe o modal
            } else {
                setErrorModal(true);
            }
        } catch (error) {
            console.error("Erro ao enviar requisição:", error);
        }
    };

    const checkEmailUniqueness = async (email) => {
        try {
            const response = await fetch(`http://localhost:9000/check-email?email=${email}`);

            if (response.ok) {
                const data = await response.json();
                return data.isUnique;
            }
        } catch (error) {
            console.error("Erro ao verificar email:", error);
        }
        return false;
    };

    return (
    <>
        <Modal show={formModal} onHide={() => setFormModal(false)}className="modal">
            <Carousel>
                <Carousel.Item className="car-item">
                    <h2>Coleta de Usuário</h2>
                    <Form className="formulario-av">
                        <h5>
                            1. Qual seu vínculo com o IFES - Campus Santa Teresa?{" "}
                            <span id="asterisco">*</span>
                        </h5>
                        <p>Marque apenas uma opção.</p>

                        {["radio"].map((type) => (
                            <div key={`inline-${type}`} className="mb-3">
                                <Form.Check
                                    inline
                                    label="Aluno do ensino médio"
                                    name="group1"
                                    type={type}
                                    id={`inline-${type}-1`}
                                />
                                <Form.Check
                                    inline
                                    label="Aluno da graduação"
                                    name="group1"
                                    type={type}
                                    id={`inline-${type}-2`}
                                />
                                <Form.Check
                                    inline
                                    label="Servidor, docente ou tercerizado"
                                    name="group1"
                                    type={type}
                                    id={`inline-${type}-3`}
                                />
                            </div>
                        ))}
                    </Form>
                    <Form className="formulario-av">
                        <h5>
                            2. Qual refeição você realiza no restaurante institucional?
                            <span id="asterisco">*</span>
                        </h5>
                        <p>Marque todas que se aplicam.</p>
                        {["checkbox"].map((type) => (
                            <div key={`inline-${type}`} className="mb-4">
                                <Form.Check
                                    inline
                                    label="Café da manhã"
                                    name="group2"
                                    type={type}
                                    id={`inline-${type}-4`}
                                />
                                <Form.Check
                                    inline
                                    label="Almoço"
                                    name="group2"
                                    type={type}
                                    id={`inline-${type}-5`}
                                />
                                <Form.Check
                                    inline
                                    label="Lanche da tarde"
                                    name="group2"
                                    type={type}
                                    id={`inline-${type}-6`}
                                />
                                <Form.Check
                                    inline
                                    label="Jantar"
                                    name="group2"
                                    type={type}
                                    id={`inline-${type}-7`}
                                />
                            </div>
                        ))}
                    </Form>

                    <Form className="formulario-av">
                        <h5>
                            3. Você é vegetariano?<span id="asterisco">*</span>
                        </h5>
                        <p>Marque apenas uma opção.</p>
                        {["radio"].map((type) => (
                            <div key={`inline-${type}`} className="mb-4">
                                <Form.Check
                                    inline
                                    label="Sim"
                                    name="group3"
                                    type={type}
                                    id={`inline-${type}-9`}
                                />
                                <Form.Check
                                    inline
                                    label="Não"
                                    name="group3"
                                    type={type}
                                    id={`inline-${type}-10`}
                                />
                            </div>
                        ))}
                    </Form>
                </Carousel.Item>
            </Carousel>
        </Modal>





     <Modal show={successModal} onHide={() => setSuccessModal(false)}className="modal">
      <Modal.Header>
        <Modal.Title>Sucesso!</Modal.Title>
      </Modal.Header>
      <Modal.Body>Cadastro concluído</Modal.Body>
      <Modal.Footer>
        <Button variant="primary" onClick={() => setSuccessModal(false)}>
          Fechar
        </Button>
      </Modal.Footer>
    </Modal>

        <Modal show={errorModal} onHide={() => setErrorModal(false)} className="modal">
            <Modal.Header >
                <Modal.Title>Erro!</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {emailError ? (
                    emailError
                ) : (
                    "Erro ao cadastrar usuário"
                )}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="danger" onClick={() => setErrorModal(false)}>
                    Fechar
                </Button>
            </Modal.Footer>
        </Modal>

     <Modal show={passcheckModal} onHide={() => setPasscheckModal(false)}className="modal">
      <Modal.Header >
        <Modal.Title>Erro!</Modal.Title>
      </Modal.Header>
      <Modal.Body>Erro na confirmação de senha, por favor tente novamente</Modal.Body>
      <Modal.Footer>
        <Button variant="danger" onClick={() => setPasscheckModal(false)}>
          Fechar
        </Button>
      </Modal.Footer>
    </Modal>

      <div className="cont">
        <Row>
          <Col>
            <Form id="form-login" onSubmit={handleSubmit}>
              <h2>Cadastro</h2>
              <Form.Group className="mb-3">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Digite seu nome"
                  value={nome}
                  onChange={(e) => setNome(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Endereço de email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Digite seu email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                <Form.Text className="text-muted">
                  Nós nunca iremos compartilhar seu email com terceiros
                </Form.Text>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Senha</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Digite sua senha"
                  value={senha}
                  onChange={(e) => setSenha(e.target.value)}
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Confirmar senha</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Confirme sua senha"
                  value={confirmarSenha}
                  onChange={(e) => setConfirmarSenha(e.target.value)}
                />
              </Form.Group>
              <Button type="submit" id="button-login-signup">
                Cadastrar
              </Button>
              <div>
                 <Link to="/login">Já possui conta? Faça login</Link>
              </div>
            </Form>

          </Col>
        </Row>
      </div>
    </>
  );
};

export default SignUpConst;
