import "../Sass/_avaliacoes.scss";
import React, { useState } from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';
import Pagination from 'react-bootstrap/Pagination';

const Avaliacoes = () => {
  const [opcaoSelecionada, setOpcaoSelecionada] = useState('');

  const handleOpcaoChange = (event) => {
    setOpcaoSelecionada(event.target.value);
  };

  const handleNextClick = () => {
    if (opcaoSelecionada) {
      // Avance para a próxima etapa
    } else {
      // Exiba uma mensagem de erro ou tome a ação apropriada
    }
  };

  return (
    <>
      <Container className="cont2">
        <Row className="row">
          <Col id="pg-prev">
            <Pagination>
              <Pagination.Prev href="/">Voltar</Pagination.Prev>
            </Pagination>
          </Col>
        </Row>
        <h2>Avaliação</h2>
        <Row className="row">
          <Col>
            <Form>
              <h3>O que você escolheu hoje de principal?</h3>
              {['carne', 'vegetariana', 'nada'].map((opcao) => (
                <div key={opcao} className="mb-3">
                  <Form.Check
                    inline
                    label={opcao === 'carne' ? 'Carne' : opcao === 'vegetariana' ? 'Opção vegetariana' : 'Nada'}
                    name="group1"
                    type="radio"
                    value={opcao}
                    id={`inline-${opcao}`}
                    checked={opcaoSelecionada === opcao} // Verifica se essa opção está selecionada
                    onChange={handleOpcaoChange}
                  />
                </div>
              ))}
            </Form>
          </Col>
        </Row>
        <Row className="row">
          <Col id="pg-next">
            <Pagination>
              <Pagination.Next
                href="/avaliacoes/principal"
                onClick={handleNextClick}
                disabled={!opcaoSelecionada}
              >
                Próximo
              </Pagination.Next>
            </Pagination>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Avaliacoes;
